package loops;

public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// initialization //condition //increment or decrement
		for (int i = 1; i <= 5; i++) {
			System.out.println(i);
		}
		for (int i = 5; i >= 1; i--) {
			System.out.println(i);
		}
	}
}
